import{_ as a,c as e,o as s,aj as t}from"./chunks/framework.B4uOnG9Z.js";const u=JSON.parse('{"title":"SQL 约束","description":"","frontmatter":{"title":"SQL 约束","date":"2019-08-20 20:21:48","tags":["database","odoo"],"categories":["database"]},"headers":[],"relativePath":"posts/database/SQL-constraints.md","filePath":"posts/database/SQL-constraints.md","lastUpdated":1719555429000}'),o={name:"posts/database/SQL-constraints.md"},i=t('<h1 id="sql-约束" tabindex="-1">SQL 约束 <a class="header-anchor" href="#sql-约束" aria-label="Permalink to &quot;SQL 约束&quot;">​</a></h1><h2 id="sql-语句-约束" tabindex="-1">SQL 语句 约束 <a class="header-anchor" href="#sql-语句-约束" aria-label="Permalink to &quot;SQL 语句 约束&quot;">​</a></h2><blockquote><p>约束用于限制加入表的数据的类型。</p></blockquote><h2 id="sql-unique-约束" tabindex="-1">SQL UNIQUE 约束 <a class="header-anchor" href="#sql-unique-约束" aria-label="Permalink to &quot;SQL UNIQUE 约束&quot;">​</a></h2><blockquote><p><code>UNIQUE</code> 约束唯一标识数据库表中的每条记录</p></blockquote><h2 id="sql-check-约束" tabindex="-1">SQL CHECK 约束 <a class="header-anchor" href="#sql-check-约束" aria-label="Permalink to &quot;SQL CHECK 约束&quot;">​</a></h2><blockquote><p><code>CHECK</code> 约束用于限制列中的值的范围<br> 如果对单个列定义 CHECK 约束，那么该列只允许特定的值<br> 如果对一个表定义 CHECK 约束，那么此约束会在特定列中对值进行限制。</p></blockquote><h2 id="sql-not-null-约束" tabindex="-1">SQL NOT NULL 约束 <a class="header-anchor" href="#sql-not-null-约束" aria-label="Permalink to &quot;SQL NOT NULL 约束&quot;">​</a></h2><blockquote><p><code>NOT NULL</code> 约束强制列不接受 <code>NULL</code> 值<br><code>NOT NULL</code> 约束强制字段始终包含值</p></blockquote><h2 id="sql-primary-key-约束" tabindex="-1">SQL PRIMARY KEY 约束 <a class="header-anchor" href="#sql-primary-key-约束" aria-label="Permalink to &quot;SQL PRIMARY KEY 约束&quot;">​</a></h2><blockquote><p>PRIMARY KEY 约束唯一标识数据库表中的每条记录<br> 主键必须包含唯一的值。主键列不能包含 NULL 值</p></blockquote><h2 id="sql-foreign-key-约束" tabindex="-1">SQL FOREIGN KEY 约束 <a class="header-anchor" href="#sql-foreign-key-约束" aria-label="Permalink to &quot;SQL FOREIGN KEY 约束&quot;">​</a></h2><blockquote><p>一个表中的 FOREIGN KEY 指向另一个表中的 PRIMARY KEY</p></blockquote><h2 id="sql-default-约束" tabindex="-1">SQL DEFAULT 约束 <a class="header-anchor" href="#sql-default-约束" aria-label="Permalink to &quot;SQL DEFAULT 约束&quot;">​</a></h2><blockquote><p><code>DEFAULT</code> 约束用于向列中插入默认值<br> 如果没有规定其他的值，那么会将默认值添加到所有的新记录</p></blockquote><h2 id="odoo-python-中的-sql-约束" tabindex="-1">odoo python 中的 sql 约束 <a class="header-anchor" href="#odoo-python-中的-sql-约束" aria-label="Permalink to &quot;odoo python 中的 sql 约束&quot;">​</a></h2><ul><li>内部的基本元素是一个(name, sql_definition, message)的元祖 <ul><li><code>@name</code>: 该约束的名称</li><li><code>@sql_definition</code>: 定义在数据库中的函数</li><li><code>@message</code>: 如果违反在数据库中的约束，将会显示的消息</li></ul></li></ul><div class="language-python vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">python</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">_sql_constraints </span><span style="--shiki-light:#D73A49;--shiki-dark:#F97583;">=</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> [(</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;uniq_name&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;unique(name)&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&#39;name must be unique !&#39;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">)]</span></span></code></pre></div>',18),l=[i];function n(d,h,r,c,p,k){return s(),e("div",null,l)}const b=a(o,[["render",n]]);export{u as __pageData,b as default};
