import{_ as t,c as e,o as a,aj as p}from"./chunks/framework.q8cpVy6V.js";const m=JSON.parse('{"title":"https 的握手过程","description":"","frontmatter":{"title":"https 的握手过程","date":"2021-05-24 21:58:25","categories":["http"],"tags":["http"]},"headers":[],"relativePath":"posts/http/https-shakehand.md","filePath":"posts/http/https-shakehand.md","lastUpdated":1720157874000}'),s={name:"posts/http/https-shakehand.md"},h=p('<h1 id="https-的握手过程" tabindex="-1">https 的握手过程 <a class="header-anchor" href="#https-的握手过程" aria-label="Permalink to &quot;https 的握手过程&quot;">​</a></h1><p><a href="https://www.cnblogs.com/fengfengyang/p/9852481.html" target="_blank" rel="noreferrer">参考</a></p><ol><li><p>客户端提交https请求</p></li><li><p>服务器响应客户,并把服务器公钥发给客户端</p></li><li><p>客户端验证公钥的有效性</p></li><li><p>有效后，客户端会生成一个会话密钥(一个随机数)</p></li><li><p>用服务器公钥加密这个会话密钥后，发送给服务器</p></li><li><p>服务器收到公钥加密的密钥后，用私钥解密，获取会话密钥</p></li><li><p>客户端与服务器利用会话密钥对传输数据进行对称加密通信</p></li></ol>',3),l=[h];function o(i,r,n,_,c,d){return a(),e("div",null,l)}const g=t(s,[["render",o]]);export{m as __pageData,g as default};
